import React, { useState } from 'react';
import { request } from '../axios_helper';

function NewProfile() {
  const [address, setAddress] = useState('');
  const [telephone, setTelephone] = useState('');
  const [surname, setSurname] = useState('');
  const [firstname, setFirstname] = useState('');
  const [patronymic, setPatronymic] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    const newProfile = {
      address: address,
      telephone: telephone,
      surname: surname,
      firstname: firstname,
      patronymic: patronymic
    };

    request('post', '/profiles', newProfile)
      .then(response => {
        if (response.status === 201) {
          // Профиль успешно создан
          // Сбрасываем значения полей формы
          setAddress('');
          setTelephone('');
          setSurname('');
          setFirstname('');
          setPatronymic('');
          // Дополнительные действия после создания профиля
          // Например, обновление списка профилей или переход на другую страницу
        } else {
          console.error('Ошибка при создании профиля');
        }
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Добавить новый профиль</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block mb-1">Адрес:</label>
          <input
            type="text"
            value={address}
            onChange={event => setAddress(event.target.value)}
            required
            className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-1">Телефон:</label>
          <input
            type="text"
            value={telephone}
            onChange={event => setTelephone(event.target.value)}
            required
            className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-1">Фамилия:</label>
          <input
            type="text"
            value={surname}
            onChange={event => setSurname(event.target.value)}
            required
            className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-1">Имя:</label>
          <input
            type="text"
            value={firstname}
            onChange={event => setFirstname(event.target.value)}
            required
            className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-1">Отчество:</label>
          <input
            type="text"
            value={patronymic}
            onChange={event => setPatronymic(event.target.value)}
            required
            className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1"
          />
        </div>
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
        >
          Создать профиль
        </button>
      </form>
    </div>
  );
}

export default NewProfile;
