import React, { useEffect, useState } from 'react';
import { request } from '../axios_helper';
import { NavLink } from 'react-router-dom';

function Services() {
  const [types, setTypes] = useState([]);
  const [selectedType, setSelectedType] = useState(null);
  const [area, setArea] = useState(30);
  const [services, setServices] = useState([]);
  const [profiles, setProfiles] = useState([]);

  useEffect(() => {
    request('get', '/types')
      .then(response => {
        setTypes(response.data);
        setSelectedType(response.data[0]);
      })
      .catch(error => console.error(error));

    setArea(30); // Установка значения площади по умолчанию равным 30

    request('get', '/services')
      .then(response => {
        setServices(response.data);
      })
      .catch(error => console.error(error));

    request('get', '/profiles')
      .then(response => {
        setProfiles(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleTypeChange = (event) => {
    const typeId = event.target.value;
    const selected = types.find(type => type.id === parseInt(typeId));
    setSelectedType(selected);
  };

  const handleAreaChange = (event) => {
    const areaValue = event.target.value;
    setArea(areaValue);
  };

  const handleServiceCountChange = (event, serviceId) => {
    const countValue = event.target.value;
    setServices(prevServices => {
      return prevServices.map(service => {
        if (service.id === serviceId) {
          return { ...service, count: countValue };
        }
        return service;
      });
    });
  };

  const calculatePrice = () => {
    let totalPrice = 0;
    if (selectedType && area > 0) {
      totalPrice += selectedType.price * area;
    }
    services.forEach(service => {
      const servicePrice = service.price || 0;
      const serviceCount = service.count || 0;
      totalPrice += servicePrice * serviceCount;
    });
    return totalPrice;
  };

  return (
    <div className="max-w-screen-xl mx-auto p-4">
      <div className="flex">
        <div className="w-2/3 pr-4 mr-12">
          <label className="mr-2">Тип уборки:</label>
          <select
            className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1 mr-4 mb-4"
            onChange={handleTypeChange}
          >
            {types.map(type => (
              <option key={type.id} value={type.id}>
                {type.name}
              </option>
            ))}
          </select>
          <div className="flex items-center">
            <label className="mr-2">Площадь:</label>
            <input
              type="number"
              value={area}
              onChange={handleAreaChange}
              min={30}
              max={500}
              className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1"
            />
          </div>

          <div className="mt-4">
            <h2 className="text-xl font-bold mb-2 text-gray-800">Дополнительные услуги</h2>
            <div className="space-y-2 text-base">
              {services.map(service => (
                <div key={service.id} className="border-b-2 border-blue-400 p-2 flex items-center">
                  <p className="font-bold text-gray-700 w-1/2">{service.name}</p>
                  <p className="mr-1">{service.price}</p>
                  <p className="w-1/2">{service.unit}</p>
                  <input
                    type="number"
                    min={0}
                    max={100}
                    value={service.count || 0}
                    onChange={event => handleServiceCountChange(event, service.id)}
                    className="focus:outline-blue-600 border border-blue-500 px-1 rounded ml-auto"
                  />
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="w-1/3 p-2">
          {selectedType && (
            <div className="mt-4 flex border-2 border-green-500 shadow-2xl rounded p-4">
              <p className="font-bold text-gray-800 text-xl mb-2 mr-2">Итого к оплате:</p>
              <p className="font-bold text-blue-500 text-xl ml-auto">{calculatePrice()} руб.</p>
            </div>
          )}
          <div className="mt-6">
            <label className="mr-2">Укажите адресс, номер телефона и ФИО:</label>
            <select
              className="border border-blue-500 focus:outline-blue-600 focus:bg-yellow-400 rounded px-4 py-1"
            // onChange={handleProfileChange}
            >
              {profiles.map(profile => (
                <option key={profile.id} value={profile.id}>
                  {profile.address} +{profile.telephone} {profile.surname} {profile.firstname[0]}. {profile.patronymic[0]}.
                </option>
              ))}
            </select>
            <NavLink to="/newprofile" className="text-blue-700 border-b border-blue-700 hover:font-semibold">Создать новый профиль</NavLink>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Services;
