import React from 'react';
import { NavLink } from 'react-router-dom';
import { request, setAuthHeader } from '../axios_helper';
import AuthContent from './AuthContent';
import LoginForm from './LoginForm';

export default class Navbar extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      componentToShow: "welcome"
    }
  };

  login = () => {
    this.setState({ componentToShow: "login" })
  };

  logout = () => {
    this.setState({ componentToShow: "welcome" })
    setAuthHeader(null);
  };

  onLogin = (e, email, password) => {
    e.preventDefault();
    request(
      "POST",
      "/login",
      {
        email: email,
        password: password
      }).then(
        (response) => {
          setAuthHeader(response.data.token);
          this.setState({ componentToShow: "messages" });
        }).catch(
          (error) => {
            setAuthHeader(null);
            this.setState({ componentToShow: "welcome" })
          }
        );
  };

  onRegister = (event, surname, firstname, patronymic, email, password) => {
    event.preventDefault();
    request(
      "POST",
      "/register",
      {
        surname: surname,
        firstname: firstname,
        patronymic: patronymic,
        email: email,
        password: password
      }).then(
        (response) => {
          setAuthHeader(response.data.token);
          this.setState({ componentToShow: "messages" });
        }).catch(
          (error) => {
            setAuthHeader(null);
            this.setState({ componentToShow: "welcome" })
          }
        );
  };


  render() {
    return (<nav className="bg-white border-gray-200">
      <div className="max-w-screen-xl flex flex-wrap items-center justify-between mx-auto p-4">
        <a href="/" className="flex items-center">
          <span className="self-center text-2xl font-semibold whitespace-nowrap">Cleaning service</span>
        </a>
        <div className="hidden w-full md:block md:w-auto" id="navbar-default">
          <ul className="font-medium flex flex-col p-4 md:p-0 mt-4 md:flex-row md:space-x-8 md:mt-0">
            <li>
              <NavLink
                to="/main"
                className="block py-2 pl-3 pr-4 text-white bg-blue-700 rounded md:bg-transparent md:text-blue-700 md:p-0"
                aria-current="page"
              >
                Главная
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/contacts"
                className="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
              >
                Контакты
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/servicelist"
                className="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
              >
                Услуги
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/myapps"
                className="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
              >
                Личный кабинет
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/cart"
                className="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
              >
                Корзина
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/"
                onClick={this.logout()}
                className="block py-2 pl-3 pr-4 ml-16 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
              >
                Выйти
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/"
                onClick={this.login()}
                className="block py-2 pl-3 pr-4 ml-16 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
              >
                Войти
              </NavLink>
            </li>
          </ul>
        </div>
      </div>
    </nav>
    );
  };
};
