import React, { useEffect, useState } from 'react';
import { request } from '../axios_helper';

function Services() {
  const [types, setTypes] = useState([]);
  const [selectedType, setSelectedType] = useState(null);
  const [area, setArea] = useState(30);
  const [services, setServices] = useState([]);

  useEffect(() => {
    request('get', '/types')
      .then(response => {
        setTypes(response.data);
        setSelectedType(response.data[0]);
      })
      .catch(error => console.error(error));

    setArea(30); // Установка значения площади по умолчанию равным 30

    request('get', '/services')
      .then(response => {
        setServices(response.data);
      })
      .catch(error => console.error(error));
  }, []);

  const handleTypeChange = (event) => {
    const typeId = event.target.value;
    const selected = types.find(type => type.id === parseInt(typeId));
    setSelectedType(selected);
  };

  const handleAreaChange = (event) => {
    const areaValue = event.target.value;
    setArea(areaValue);
  };

  const calculatePrice = () => {
    if (selectedType && area > 0) {
      return selectedType.price * area;
    }
    return 0;
  };

  return (
    <div className="max-w-screen-xl mx-auto p-4">
      <div className="flex">
        <div className="w-2/3 pr-4 mr-12">
          <label className="mr-2">Тип уборки:</label>
          <select
            className="border border-gray-300 rounded px-4 py-1 mr-4 mb-4"
            onChange={handleTypeChange}
          >
            {types.map(type => (
              <option key={type.id} value={type.id}>
                {type.name}
              </option>
            ))}
          </select>
          <div className="flex items-center">
            <label className="mr-2">Площадь:</label>
            <input
              type="number"
              value={area}
              onChange={handleAreaChange}
              min={30}
              max={500}
              className="border border-gray-300 rounded px-4 py-1"
            />
          </div>
          
          <div className="mt-4">
            <h2 className="text-xl font-bold mb-2 text-gray-800">Дополнительные услуги</h2>
            <div className="space-y-2 text-base">
              {services.map(service => (
                <div key={service.id} className="border rounded p-2 flex items-center">
                  <p className="font-bold text-gray-700 w-1/2">{service.name}</p>
                  <p className="mr-1">{service.price}</p>
                  <p className="w-1/2">{service.unit}</p>
                  <input
                    type="number"
                    min={0}
                    max={100}
                    defaultValue={0}
                    className="focus:outline-gray-400 border rounded ml-auto"
                  />
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="w-1/3">
          {selectedType && (
            <div className="mt-4">
              <p className="font-bold text-green-600 text-xl mb-2">Итого к оплате:</p>
              <p className="font-bold text-blue-600 text-xl">{calculatePrice()}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Services;
