import React, { useEffect, useState } from 'react';
import { request } from '../axios_helper';

function Services() {
  const [types, setTypes] = useState([]);
  const [selectedType, setSelectedType] = useState(null);
  const [area, setArea] = useState(30);

  useEffect(() => {
    request('get', '/types')
      .then(response => {
        setTypes(response.data);
        setSelectedType(response.data[0]);
      })
      .catch(error => console.error(error));

    setArea(30); // Установка значения площади по умолчанию равным 30
  }, []);

  const handleTypeChange = (event) => {
    const typeId = event.target.value;
    const selected = types.find(type => type.id === parseInt(typeId));
    setSelectedType(selected);
  };

  const handleAreaChange = (event) => {
    const areaValue = event.target.value;
    setArea(areaValue);
  };

  const calculatePrice = () => {
    if (selectedType && area > 0) {
      return selectedType.price * area;
    }
    return 0;
  };

  return (
    <div className="max-w-screen-xl mx-auto p-4">
      <select
        className="border border-gray-300 rounded px-4 py-2 mr-4"
        onChange={handleTypeChange}
      >
        {types.map(type => (
          <option key={type.id} value={type.id}>
            {type.name}
          </option>
        ))}
      </select>
      <div className="flex items-center">
        <label className="mr-2">Площадь:</label>
        <input
          type="number"
          value={area}
          onChange={handleAreaChange}
          min={30}
          max={500}
          className="border border-gray-300 rounded px-4 py-2"
        />
      </div>
      {selectedType && (
        <p className="mt-4">Цена: {calculatePrice()}</p>
      )}
    </div>
  );
}

export default Services;
