import React, { useEffect, useState } from 'react';
import { request } from '../axios_helper';

const ClientAppTable = () => {
    const [apps, setApps] = useState([]);

    const formatDate = (date) => {
        const options = { day: '2-digit', month: '2-digit', year: 'numeric', hour: 'numeric', minute: 'numeric' };
        return new Intl.DateTimeFormat('ru-RU', options).format(new Date(date));
      };
      
  
    useEffect(() => {
      // Получите заявки клиента с помощью запроса к серверу
      const fetchApps = async () => {
        try {
          const response = await request('get', '/apps');
          setApps(response.data);
          console.log(apps);
        } catch (error) {
          console.error(error);
        }
      };
  
      fetchApps();
    }, []);
  
    return (
      <div>
        <h2>Заявки клиента</h2>
        <table>
          <thead>
            <tr>
              <th>Дата подачи</th>
              <th>Дата выполнения</th>
              <th>Адрес</th>
              <th>ФИО</th>
              <th>Статус</th>
              <th>Тип</th>
            </tr>
          </thead>
          <tbody>
            {apps.map((app) => (
              <tr key={app.id}>
                <td>{formatDate(app.dateOfSubmission)}</td>
                <td>{formatDate(app.dateOfCompletion)}</td>
                <td>{app.address}</td>
                <td>{app.surname} {app.firstname[0]}. {app.patronymic[0]}.</td>
                <td>{app.status}</td>
                <td>{app.type}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };
  
  export default ClientAppTable;
