import React, { useState } from 'react';
import { request } from '../axios_helper';

function NewProfile() {
  const [address, setAddress] = useState('');
  const [telephone, setTelephone] = useState('');
  const [surname, setSurname] = useState('');
  const [firstname, setFirstname] = useState('');
  const [patronymic, setPatronymic] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    const newProfile = {
      address: address,
      telephone: telephone,
      surname: surname,
      firstname: firstname,
      patronymic: patronymic
    };

    request('post', '/profiles', newProfile)
      .then(response => {
        if (response.status === 201) {
          // Профиль успешно создан
          // Сбрасываем значения полей формы
          setAddress('');
          setTelephone('');
          setSurname('');
          setFirstname('');
          setPatronymic('');
          // Дополнительные действия после создания профиля
          // Например, обновление списка профилей или переход на другую страницу
        } else {
          console.error('Ошибка при создании профиля');
        }
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div className="flex justify-center items-center mt-8 text-gray-800">
      <div className="max-w-md w-full">
        <h1 className="text-2xl font-bold mb-4 text-center">Добавить новый профиль</h1>
        <form onSubmit={handleSubmit} className="border-x-2 border-blue-500 px-4">
          <div className="mb-4 flex">
            <label className="w-20 mr-2 mb-1">Адрес:</label>
            <input
              type="text"
              value={address}
              onChange={event => setAddress(event.target.value)}
              required
              className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1 flex-1"
            />
          </div>
          <div className="mb-4 flex">
            <label className="w-20 mr-2 mb-1">Телефон:</label>
            <input
              type="text"
              value={telephone}
              onChange={event => setTelephone(event.target.value)}
              required
              className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1 flex-1"
            />
          </div>
          <div className="mb-4 flex">
            <label className="w-20 mr-2 mb-1">Фамилия:</label>
            <input
              type="text"
              value={surname}
              onChange={event => setSurname(event.target.value)}
              required
              className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1 flex-1"
            />
          </div>
          <div className="mb-4 flex">
            <label className="w-20 mr-2 mb-1">Имя:</label>
            <input
              type="text"
              value={firstname}
              onChange={event => setFirstname(event.target.value)}
              required
              className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1 flex-1"
            />
          </div>
          <div className="mb-4 flex">
            <label className="w-20 mr-2 mb-1">Отчество:</label>
            <input
              type="text"
              value={patronymic}
              onChange={event => setPatronymic(event.target.value)}
              required
              className="border border-blue-500 focus:outline-blue-600 rounded px-4 py-1 flex-1"
            />
          </div>
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-800 text-white font-bold py-2 px-4 rounded"
          >
            Создать профиль
          </button>
        </form>
      </div>
    </div>
  );
}

export default NewProfile;
