import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { getAuthToken } from  '../axios_helper';

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(getAuthToken() !== null);

  useEffect(() => {
    const checkAuth = () => {
      setIsAuthenticated(getAuthToken() !== null);
    };

    // Прослушиваем изменения значения авторизации
    const authListener = setInterval(checkAuth, 1000);

    // Очищаем прослушиватель при размонтировании компонента
    return () => {
      clearInterval(authListener);
    };
  }, []);

  return (
    <nav className="bg-white border-gray-200">
      <div className="max-w-screen-xl flex flex-wrap items-center justify-between mx-auto p-4">
        <a href="/" className="flex items-center">
          <span className="self-center text-2xl font-semibold whitespace-nowrap">Cleaning service</span>
        </a>
        <div className="hidden w-full md:block md:w-auto" id="navbar-default">
          <ul className="font-medium flex flex-col p-4 md:p-0 mt-4 md:flex-row md:space-x-8 md:mt-0">
            <li>
              <NavLink
                to="/main"
                className="block py-2 pl-3 pr-4 text-white bg-blue-700 rounded md:bg-transparent md:text-blue-700 md:p-0"
                aria-current="page"
              >
                Главная
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/contacts"
                className="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
              >
                Контакты
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/servicelist"
                className="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
              >
                Услуги
              </NavLink>
            </li>
            {isAuthenticated && (
              <>
                <li>
                  <NavLink
                    to="/myapps"
                    className="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
                  >
                    Личный кабинет
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/cart"
                    className="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
                  >
                    Корзина
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/authorize"
                    className="block py-2 pl-3 pr-4 ml-16 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
                  >
                    Выйти
                  </NavLink>
                </li>
              </>
            )}
            {!isAuthenticated && (
              <li>
                <NavLink
                  to="/login"
                  className="block py-2 pl-3 pr-4 ml-16 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0"
                >
                  Войти
                </NavLink>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
