import React, { useEffect, useState } from 'react';
import { request } from '../axios_helper';

function Services() {
    const [types, setTypes] = useState([]);
    const [selectedType, setSelectedType] = useState(null);
    const [area, setArea] = useState(30);
    const [services, setServices] = useState([]);

    useEffect(() => {
        request('get', '/types')
            .then(response => {
                setTypes(response.data);
                setSelectedType(response.data[0]);
            })
            .catch(error => console.error(error));

        setArea(30); // Установка значения площади по умолчанию равным 30

        request('get', '/services')
            .then(response => {
                setServices(response.data);
            })
            .catch(error => console.error(error));
    }, []);

    const handleTypeChange = (event) => {
        const typeId = event.target.value;
        const selected = types.find(type => type.id === parseInt(typeId));
        setSelectedType(selected);
    };

    const handleAreaChange = (event) => {
        const areaValue = event.target.value;
        setArea(areaValue);
    };

    const calculatePrice = () => {
        if (selectedType && area > 0) {
            return selectedType.price * area;
        }
        return 0;
    };

    return (
        <div className="max-w-screen-xl mx-auto p-4 bg-slate-300">
            <select
                className="border border-gray-300 rounded px-4 py-2 mr-4"
                onChange={handleTypeChange}
            >
                {types.map(type => (
                    <option key={type.id} value={type.id}>
                        {type.name}
                    </option>
                ))}
            </select>
            <div className="flex items-center">
                <label className="mr-2">Площадь:</label>
                <input
                    type="number"
                    value={area}
                    onChange={handleAreaChange}
                    min={30}
                    max={500}
                    className="border border-gray-300 rounded px-4 py-2"
                />
            </div>
            {selectedType && (
                <p className="mt-4">Цена: {calculatePrice()}</p>
            )}

            <div className="mt-4">
                <h2 className="text-lg font-bold mb-2 text-gray-800">Услуги</h2>
                {services.map(service => (
                    <div key={service.id} className="border rounded p-2 mb-2 flex">
                        <p className="font-bold text-gray-700 mr-12">{service.name}</p>
                        <p className="mr-1">{service.price} </p>
                        <p>{service.unit}</p>
                        <input
                            type="number"
                            min={0}
                            max={100}
                            className="border-none border-gray-300 rounded ml-auto"
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Services;
